import React, { useState } from 'react';
import { useStore } from '../store';
import { Product, Customer, Size, PaymentStatus } from '../types';
import { Plus, Search, UserPlus, CheckCircle2, ChevronRight, X } from 'lucide-react';

const Sales: React.FC = () => {
  const { products, customers, addSale, addCustomer } = useStore();

  const [step, setStep] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
  const [selectedSize, setSelectedSize] = useState<Size | ''>('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [salePrice, setSalePrice] = useState(0);
  const [paidAmount, setPaidAmount] = useState(0);

  const [searchTerm, setSearchTerm] = useState('');
  const [showNewCustomer, setShowNewCustomer] = useState(false);
  const [newCustomer, setNewCustomer] = useState({ name: '', phone: '', address: '' });

  const resetForm = () => {
    setStep(1);
    setSelectedProduct(null);
    setSelectedCustomer(null);
    setSelectedSize('');
    setSelectedColor('');
    setQuantity(1);
    setSalePrice(0);
    setPaidAmount(0);
    setSearchTerm('');
  };

  const handleAddCustomer = () => {
    const customer: Customer = {
      id: Date.now().toString(),
      ...newCustomer,
      totalSpent: 0,
      totalDue: 0
    };
    addCustomer(customer);
    setSelectedCustomer(customer);
    setShowNewCustomer(false);
    setStep(2);
  };

  const handleCompleteSale = () => {
    if (!selectedProduct || !selectedCustomer || !selectedSize || !selectedColor) return;

    const totalAmount = salePrice * quantity;
    const dueAmount = totalAmount - paidAmount;
    const profit = (salePrice - selectedProduct.purchasePrice) * quantity;
    const paymentStatus: PaymentStatus = dueAmount === 0 ? 'Full Paid' : (paidAmount === 0 ? 'Due' : 'Partial Paid');

    addSale({
      id: Date.now().toString(),
      customerId: selectedCustomer.id,
      customerName: selectedCustomer.name,
      productId: selectedProduct.id,
      productName: selectedProduct.name,
      size: selectedSize as Size,
      color: selectedColor,
      quantity,
      salePrice,
      totalAmount,
      paidAmount,
      dueAmount,
      profit,
      date: new Date().toISOString(),
      paymentStatus
    });

    setStep(6); // Success
  };

  const currentVariant = selectedProduct?.variants.find(v => v.size === selectedSize && v.color === selectedColor);
  const maxQty = currentVariant?.quantity || 0;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-bold text-slate-800">New Sale</h2>
        {step > 1 && step < 6 && (
          <button onClick={resetForm} className="text-rose-500 p-1"><X size={20}/></button>
        )}
      </div>

      {/* Step Indicators */}
      <div className="flex items-center gap-1 overflow-x-auto pb-2 no-scrollbar">
        {[1, 2, 3, 4, 5].map(s => (
          <div key={s} className={`h-1 flex-1 rounded-full min-w-[20px] transition-all ${step >= s ? 'bg-indigo-600' : 'bg-slate-200'}`}></div>
        ))}
      </div>

      {/* STEP 1: CUSTOMER SELECTION */}
      {step === 1 && (
        <div className="space-y-4 animate-in slide-in-from-right-4">
          <div className="flex items-center gap-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
              <input 
                type="text" 
                placeholder="Search customer by name or phone..." 
                className="w-full pl-10 pr-4 py-3 bg-white border border-slate-200 rounded-2xl outline-none focus:ring-2 focus:ring-indigo-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <button 
              onClick={() => setShowNewCustomer(true)}
              className="p-3 bg-indigo-600 text-white rounded-2xl hover:bg-indigo-700"
            >
              <UserPlus size={24} />
            </button>
          </div>

          <div className="space-y-3">
            {customers
              .filter(c => c.name.toLowerCase().includes(searchTerm.toLowerCase()) || c.phone.includes(searchTerm))
              .map(customer => (
                <button 
                  key={customer.id} 
                  onClick={() => { setSelectedCustomer(customer); setStep(2); }}
                  className="w-full bg-white p-4 rounded-2xl border border-slate-100 flex items-center justify-between shadow-sm active:scale-[0.98] transition-transform"
                >
                  <div className="text-left">
                    <p className="font-semibold text-slate-800">{customer.name}</p>
                    <p className="text-xs text-slate-500">{customer.phone}</p>
                  </div>
                  <ChevronRight size={18} className="text-slate-300" />
                </button>
              ))}
          </div>
        </div>
      )}

      {/* STEP 2: PRODUCT SELECTION */}
      {step === 2 && (
        <div className="space-y-4 animate-in slide-in-from-right-4">
          <h3 className="text-sm font-semibold text-slate-500 uppercase tracking-wider">Select Product</h3>
          <div className="grid grid-cols-2 gap-3">
            {products.map(product => {
              const totalStock = product.variants.reduce((acc, v) => acc + v.quantity, 0);
              return (
                <button 
                  key={product.id} 
                  onClick={() => { setSelectedProduct(product); setSalePrice(product.salePrice); setStep(3); }}
                  className="bg-white p-3 rounded-2xl border border-slate-100 shadow-sm text-left overflow-hidden group active:scale-[0.98] transition-transform"
                >
                  <img src={product.image} alt={product.name} className="w-full aspect-square object-cover rounded-xl mb-3" />
                  <h4 className="font-semibold text-slate-800 text-sm truncate">{product.name}</h4>
                  <div className="flex justify-between items-center mt-1">
                    <p className="text-indigo-600 font-bold text-sm">‡ß≥{product.salePrice}</p>
                    <p className="text-[10px] bg-slate-100 px-1.5 py-0.5 rounded-md">Stock: {totalStock}</p>
                  </div>
                </button>
              );
            })}
          </div>
        </div>
      )}

      {/* STEP 3: SIZE & COLOR */}
      {step === 3 && selectedProduct && (
        <div className="space-y-6 animate-in slide-in-from-right-4 bg-white p-6 rounded-3xl border border-slate-100 shadow-sm">
          <div>
            <label className="block text-sm font-semibold text-slate-700 mb-3">Select Size</label>
            <div className="flex flex-wrap gap-2">
              {['XS', 'S', 'M', 'L', 'XL', 'XXL'].map(sz => {
                const available = selectedProduct.variants.some(v => v.size === sz && v.quantity > 0);
                return (
                  <button 
                    key={sz}
                    disabled={!available}
                    onClick={() => setSelectedSize(sz as Size)}
                    className={`w-12 h-12 rounded-xl border flex items-center justify-center font-bold text-sm transition-all ${
                      selectedSize === sz 
                        ? 'bg-indigo-600 border-indigo-600 text-white' 
                        : available ? 'bg-white border-slate-200 text-slate-700' : 'bg-slate-50 border-slate-100 text-slate-300'
                    }`}
                  >
                    {sz}
                  </button>
                );
              })}
            </div>
          </div>

          {selectedSize && (
            <div>
              <label className="block text-sm font-semibold text-slate-700 mb-3">Select Color</label>
              <div className="flex flex-wrap gap-2">
                {selectedProduct.variants
                  .filter(v => v.size === selectedSize && v.quantity > 0)
                  .map(v => (
                    <button 
                      key={v.color}
                      onClick={() => setSelectedColor(v.color)}
                      className={`px-4 py-2 rounded-xl border text-sm font-medium transition-all ${
                        selectedColor === v.color 
                          ? 'bg-indigo-600 border-indigo-600 text-white' 
                          : 'bg-white border-slate-200 text-slate-700'
                      }`}
                    >
                      {v.color} ({v.quantity})
                    </button>
                  ))}
              </div>
            </div>
          )}

          <button 
            disabled={!selectedSize || !selectedColor}
            onClick={() => setStep(4)}
            className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold disabled:opacity-50 disabled:bg-slate-300"
          >
            Next: Quantity & Price
          </button>
        </div>
      )}

      {/* STEP 4: QUANTITY & PRICE */}
      {step === 4 && (
        <div className="space-y-6 animate-in slide-in-from-right-4 bg-white p-6 rounded-3xl border border-slate-100 shadow-sm">
          <div className="flex items-center gap-4 p-3 bg-slate-50 rounded-2xl mb-4">
            <img src={selectedProduct?.image} className="w-16 h-16 rounded-xl object-cover" />
            <div>
              <h4 className="font-bold text-slate-800">{selectedProduct?.name}</h4>
              <p className="text-xs text-slate-500">{selectedSize} ‚Ä¢ {selectedColor} ‚Ä¢ Max: {maxQty}</p>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-xs font-bold text-slate-500 uppercase">Quantity</label>
              <input 
                type="number" 
                min="1" 
                max={maxQty}
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
                className="w-full p-3 border border-slate-200 rounded-xl outline-none focus:ring-2 focus:ring-indigo-500"
              />
            </div>
            <div className="space-y-2">
              <label className="text-xs font-bold text-slate-500 uppercase">Sale Price (Per Unit)</label>
              <input 
                type="number" 
                value={salePrice}
                onChange={(e) => setSalePrice(Number(e.target.value))}
                className="w-full p-3 border border-slate-200 rounded-xl outline-none focus:ring-2 focus:ring-indigo-500 font-bold text-indigo-600"
              />
            </div>
          </div>

          <div className="p-4 bg-indigo-50 rounded-2xl flex justify-between items-center">
            <p className="font-medium text-indigo-700">Total Bill:</p>
            <p className="text-2xl font-black text-indigo-700">‡ß≥{(salePrice * quantity).toLocaleString()}</p>
          </div>

          <button 
            disabled={quantity > maxQty || quantity < 1}
            onClick={() => { setPaidAmount(salePrice * quantity); setStep(5); }}
            className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold disabled:opacity-50"
          >
            Continue to Payment
          </button>
        </div>
      )}

      {/* STEP 5: PAYMENT */}
      {step === 5 && (
        <div className="space-y-6 animate-in slide-in-from-right-4 bg-white p-6 rounded-3xl border border-slate-100 shadow-sm">
          <div className="text-center">
            <p className="text-slate-500 text-sm">Total Payable</p>
            <h3 className="text-3xl font-black text-slate-800">‡ß≥{(salePrice * quantity).toLocaleString()}</h3>
          </div>

          <div className="space-y-4">
            <div className="space-y-2">
              <label className="text-xs font-bold text-slate-500 uppercase">Paid Amount</label>
              <input 
                type="number" 
                value={paidAmount}
                onChange={(e) => setPaidAmount(Number(e.target.value))}
                className="w-full p-4 border border-slate-200 rounded-2xl outline-none focus:ring-2 focus:ring-indigo-500 text-xl font-bold"
              />
            </div>

            <div className="grid grid-cols-3 gap-2">
              <button onClick={() => setPaidAmount(salePrice * quantity)} className="py-2 bg-slate-100 rounded-xl text-xs font-bold text-slate-600">Full Paid</button>
              <button onClick={() => setPaidAmount((salePrice * quantity) / 2)} className="py-2 bg-slate-100 rounded-xl text-xs font-bold text-slate-600">50% Paid</button>
              <button onClick={() => setPaidAmount(0)} className="py-2 bg-slate-100 rounded-xl text-xs font-bold text-slate-600">Due Sale</button>
            </div>

            <div className={`p-4 rounded-2xl flex justify-between items-center border ${
              (salePrice * quantity) - paidAmount > 0 ? 'bg-rose-50 border-rose-100 text-rose-700' : 'bg-emerald-50 border-emerald-100 text-emerald-700'
            }`}>
              <p className="font-medium">Due Amount:</p>
              <p className="text-xl font-bold">‡ß≥{((salePrice * quantity) - paidAmount).toLocaleString()}</p>
            </div>
          </div>

          <button 
            onClick={handleCompleteSale}
            className="w-full py-4 bg-emerald-600 text-white rounded-2xl font-bold shadow-lg shadow-emerald-100 flex items-center justify-center gap-2"
          >
            <CheckCircle2 size={20} />
            Confirm Sale
          </button>
        </div>
      )}

      {/* STEP 6: SUCCESS */}
      {step === 6 && (
        <div className="text-center py-12 animate-in zoom-in-95">
          <div className="w-24 h-24 bg-emerald-100 text-emerald-600 rounded-full flex items-center justify-center mx-auto mb-6">
            <CheckCircle2 size={48} />
          </div>
          <h2 className="text-2xl font-black text-slate-800 mb-2">Sale Successful!</h2>
          <p className="text-slate-500 mb-8">The transaction has been recorded and stock updated.</p>
          <button 
            onClick={resetForm}
            className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold"
          >
            Create New Sale
          </button>
        </div>
      )}

      {/* NEW CUSTOMER MODAL */}
      {showNewCustomer && (
        <div className="fixed inset-0 bg-black/50 flex items-end sm:items-center justify-center z-[60] p-4">
          <div className="bg-white w-full max-w-md rounded-t-3xl sm:rounded-3xl p-6 animate-in slide-in-from-bottom-10">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-slate-800">Add New Customer</h3>
              <button onClick={() => setShowNewCustomer(false)}><X size={24} className="text-slate-400" /></button>
            </div>
            <div className="space-y-4">
              <input 
                type="text" placeholder="Name" 
                className="w-full p-4 border border-slate-200 rounded-2xl outline-none"
                value={newCustomer.name}
                onChange={(e) => setNewCustomer({...newCustomer, name: e.target.value})}
              />
              <input 
                type="tel" placeholder="Phone Number" 
                className="w-full p-4 border border-slate-200 rounded-2xl outline-none"
                value={newCustomer.phone}
                onChange={(e) => setNewCustomer({...newCustomer, phone: e.target.value})}
              />
              <input 
                type="text" placeholder="Address (Optional)" 
                className="w-full p-4 border border-slate-200 rounded-2xl outline-none"
                value={newCustomer.address}
                onChange={(e) => setNewCustomer({...newCustomer, address: e.target.value})}
              />
              <button 
                onClick={handleAddCustomer}
                className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold mt-4"
              >
                Save & Select
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sales;

import React, { useState } from 'react';
import { useStore } from '../store';
import { Product, Size, StockVariant } from '../types';
import { Plus, Search, AlertCircle, Edit, Trash2, X } from 'lucide-react';

const Stock: React.FC = () => {
  const { products, addProduct, deleteProduct } = useStore();
  const [searchTerm, setSearchTerm] = useState('');
  const [isAdding, setIsAdding] = useState(false);

  const [newProduct, setNewProduct] = useState<Partial<Product>>({
    name: '',
    category: 'General',
    purchasePrice: 0,
    salePrice: 0,
    image: 'https://picsum.photos/seed/fashion/400/400',
    variants: [
      { size: 'M', color: 'Standard', quantity: 0 }
    ]
  });

  const handleSave = () => {
    if (newProduct.name) {
      addProduct({
        id: Date.now().toString(),
        name: newProduct.name,
        category: newProduct.category || 'General',
        image: newProduct.image || 'https://picsum.photos/seed/fashion/400/400',
        purchasePrice: newProduct.purchasePrice || 0,
        salePrice: newProduct.salePrice || 0,
        variants: newProduct.variants as StockVariant[]
      });
      setIsAdding(false);
      setNewProduct({
        name: '',
        category: 'General',
        purchasePrice: 0,
        salePrice: 0,
        variants: [{ size: 'M', color: 'Standard', quantity: 0 }]
      });
    }
  };

  const addVariantField = () => {
    setNewProduct({
      ...newProduct,
      variants: [...(newProduct.variants || []), { size: 'M', color: '', quantity: 0 }]
    });
  };

  const filteredProducts = products.filter(p => 
    p.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    p.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-bold text-slate-800">Inventory Management</h2>
