                    <Plus size={14}/> Add Variant
                  </button>
                </div>
                {newProduct.variants?.map((v, idx) => (
                  <div key={idx} className="grid grid-cols-3 gap-2">
                    <select 
                      className="p-2 border border-slate-200 rounded-lg text-sm outline-none"
                      value={v.size}
                      onChange={(e) => {
                        const updated = [...(newProduct.variants || [])];
                        updated[idx].size = e.target.value as Size;
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    >
                      {['XS', 'S', 'M', 'L', 'XL', 'XXL'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>
                    <input 
                      type="text" placeholder="Color" className="p-2 border border-slate-200 rounded-lg text-sm outline-none"
                      value={v.color}
                      onChange={(e) => {
                        const updated = [...(newProduct.variants || [])];
                        updated[idx].color = e.target.value;
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    />
                    <input 
                      type="number" placeholder="Qty" className="p-2 border border-slate-200 rounded-lg text-sm outline-none"
                      value={v.quantity}
                      onChange={(e) => {
                        const updated = [...(newProduct.variants || [])];
                        updated[idx].quantity = Number(e.target.value);
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    />
                  </div>
                ))}
              </div>
            </div>

            <button 
              onClick={handleSave}
              className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold shadow-lg shadow-indigo-100 mt-4"
            >
              Save Product
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stock;

import React, { useState, useRef, useEffect } from 'react';
import { useStore } from '../store';
import { getBusinessInsights } from '../services/gemini';
import { Send, Bot, User, Sparkles, AlertCircle } from 'lucide-react';

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

const AIAssistant: React.FC = () => {
  const state = useStore();
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: 'Hello! I am your Sylsas Business Assistant. How can I help you manage your fashion store today?',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMsg: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await getBusinessInsights(state, input);
      const aiMsg: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: response,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, aiMsg]);
    } catch (err) {
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const suggestions = [
    "Is my business growing?",
    "Top selling products?",
    "Any low stock items?",
    "How is my profit today?"
  ];

  return (
    <div className="flex flex-col h-[calc(100vh-140px)]">
      <div className="bg-indigo-600 p-6 rounded-t-3xl text-white">
        <div className="flex items-center gap-3 mb-2">
          <div className="w-10 h-10 bg-white/20 backdrop-blur-md rounded-2xl flex items-center justify-center">
            <Bot size={24} />
          </div>
          <div>
            <h2 className="font-bold text-lg">Sylsas AI Assistant</h2>
            <p className="text-xs text-indigo-100 flex items-center gap-1">
              <Sparkles size={12} /> Real-time Analytics Expert
            </p>
          </div>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-slate-50 no-scrollbar">
        {messages.map(msg => (
          <div key={msg.id} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[85%] flex gap-3 ${msg.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
              <div className={`w-8 h-8 shrink-0 rounded-full flex items-center justify-center ${
                msg.role === 'user' ? 'bg-indigo-100 text-indigo-600' : 'bg-white border border-slate-200 text-slate-600 shadow-sm'
              }`}>
                {msg.role === 'user' ? <User size={16} /> : <Bot size={16} />}
              </div>
              <div className={`p-4 rounded-3xl text-sm leading-relaxed shadow-sm ${
                msg.role === 'user' 
                  ? 'bg-indigo-600 text-white rounded-tr-none' 
                  : 'bg-white text-slate-800 border border-slate-100 rounded-tl-none whitespace-pre-wrap'
              }`}>
                {msg.content}
              </div>
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white border border-slate-100 p-4 rounded-3xl rounded-tl-none shadow-sm flex items-center gap-2">
              <div className="flex gap-1">
                <div className="w-1.5 h-1.5 bg-indigo-400 rounded-full animate-bounce"></div>
                <div className="w-1.5 h-1.5 bg-indigo-400 rounded-full animate-bounce [animation-delay:0.2s]"></div>
                <div className="w-1.5 h-1.5 bg-indigo-400 rounded-full animate-bounce [animation-delay:0.4s]"></div>
              </div>
              <span className="text-xs text-slate-400 font-medium">Analyzing business data...</span>
            </div>
          </div>
        )}
        <div ref={scrollRef} />
      </div>

      <div className="p-4 bg-white border-t border-slate-100 rounded-b-3xl">
        <div className="flex gap-2 overflow-x-auto pb-4 no-scrollbar">
          {suggestions.map(s => (
            <button 
              key={s} 
              onClick={() => setInput(s)}
              className="whitespace-nowrap px-4 py-2 bg-slate-100 text-slate-600 rounded-full text-xs font-medium border border-slate-200 hover:bg-slate-200 transition-colors"
            >
              {s}
            </button>
          ))}
        </div>
        <div className="flex items-center gap-2">
          <input 
            type="text" 
            placeholder="Ask anything about your business..." 
            className="flex-1 bg-slate-50 border border-slate-200 rounded-2xl px-4 py-3 text-sm outline-none focus:ring-2 focus:ring-indigo-500 transition-all"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          />
          <button 
            onClick={handleSend}
            disabled={!input.trim() || isLoading}
            className="p-3 bg-indigo-600 text-white rounded-2xl hover:bg-indigo-700 disabled:bg-slate-300 shadow-lg shadow-indigo-100 transition-all active:scale-95"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default AIAssistant;

import React, { useState } from 'react';
import { useStore } from '../store';
import { Expense } from '../types';
import { Plus, Receipt, Calendar, Trash2, X, PlusCircle } from 'lucide-react';

const Expenses: React.FC = () => {
