import { Product, Customer, Sale, Expense, BusinessState } from './types';
import { db } from './services/firebase';
import { 
  collection, 
  onSnapshot, 
  addDoc, 
  updateDoc, 
  deleteDoc, 
  doc, 
  query, 
  orderBy 
} from 'firebase/firestore';

interface StoreContextType extends BusinessState {
  addProduct: (product: Product) => Promise<void>;
  updateProduct: (product: Product) => Promise<void>;
  deleteProduct: (id: string) => Promise<void>;
  addSale: (sale: Sale) => Promise<void>;
  addExpense: (expense: Expense) => Promise<void>;
  updateCustomer: (customer: Customer) => Promise<void>;
  addCustomer: (customer: Customer) => Promise<void>;
  error: string | null;
}

const StoreContext = createContext<StoreContextType | undefined>(undefined);

export const StoreProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [sales, setSales] = useState<Sale[]>([]);
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [admin] = useState({
    name: 'Sylsas Admin',
    phone: '0123456789',
    role: 'Owner',
    image: 'https://picsum.photos/seed/admin/100/100'
  });

  // Real-time Listeners with Error Handling
  useEffect(() => {
    const handleFirestoreError = (err: any) => {
      console.error("Firestore sync error:", err);
      if (err.code === 'permission-denied') {
        setError("Firebase Firestore API is disabled or rules are strict. Please enable it in Firebase Console.");
      } else {
        setError(err.message);
      }
    };

    const qProducts = query(collection(db, 'products'));
    const unsubProducts = onSnapshot(qProducts, (snapshot) => {
      setProducts(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Product)));
      setError(null);
    }, handleFirestoreError);

    const qCustomers = query(collection(db, 'customers'));
    const unsubCustomers = onSnapshot(qCustomers, (snapshot) => {
      setCustomers(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Customer)));
    }, handleFirestoreError);

    const qSales = query(collection(db, 'sales'), orderBy('date', 'desc'));
    const unsubSales = onSnapshot(qSales, (snapshot) => {
      setSales(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Sale)));
    }, handleFirestoreError);

    const qExpenses = query(collection(db, 'expenses'), orderBy('date', 'desc'));
    const unsubExpenses = onSnapshot(qExpenses, (snapshot) => {
      setExpenses(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Expense)));
    }, handleFirestoreError);

    return () => {
      unsubProducts();
      unsubCustomers();
      unsubSales();
      unsubExpenses();
    };
  }, []);

  const addProduct = async (product: Product) => {
    try {
      await addDoc(collection(db, 'products'), product);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const updateProduct = async (product: Product) => {
    try {
      const docRef = doc(db, 'products', product.id);
      await updateDoc(docRef, { ...product });
    } catch (err: any) {
      setError(err.message);
    }
  };

  const deleteProduct = async (id: string) => {
    try {
      await deleteDoc(doc(db, 'products', id));
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addCustomer = async (customer: Customer) => {
    try {
      await addDoc(collection(db, 'customers'), customer);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const updateCustomer = async (customer: Customer) => {
    try {
      const docRef = doc(db, 'customers', customer.id);
      await updateDoc(docRef, { ...customer });
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addSale = async (sale: Sale) => {
    try {
      await addDoc(collection(db, 'sales'), sale);
      const product = products.find(p => p.id === sale.productId);
      if (product) {
        const updatedVariants = product.variants.map(v => {
          if (v.size === sale.size && v.color === sale.color) {
            return { ...v, quantity: v.quantity - sale.quantity };
          }
          return v;
        });
        await updateDoc(doc(db, 'products', product.id), { variants: updatedVariants });
      }
      const customer = customers.find(c => c.id === sale.customerId);
      if (customer) {
        await updateDoc(doc(db, 'customers', customer.id), {
          totalSpent: customer.totalSpent + sale.totalAmount,
          totalDue: customer.totalDue + sale.dueAmount
        });
      }
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addExpense = async (expense: Expense) => {
    try {
      await addDoc(collection(db, 'expenses'), expense);
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <StoreContext.Provider value={{
      products,
      customers,
      sales,
      expenses,
      admin,
      addProduct,
      updateProduct,
      deleteProduct,
      addSale,
      addExpense,
      updateCustomer,
      addCustomer,
      error
    }}>
      {children}
    </StoreContext.Provider>
  );
};

export const useStore = () => {
  const context = useContext(StoreContext);
  if (!context) throw new Error("useStore must be used within StoreProvider");
  return context;
};

import React, { useState } from 'react';
import { StoreProvider, useStore } from './store';
import { TabType } from './types';
import Dashboard from './components/Dashboard';
import Sales from './components/Sales';
import Stock from './components/Stock';
import Expenses from './components/Expenses';
import Customers from './components/Customers';
import Reports from './components/Reports';
import AIAssistant from './components/AIAssistant';
import { 
  LayoutDashboard, 
  ShoppingCart, 
  Package, 
  Receipt, 
  Users, 
  BarChart3, 
  BotMessageSquare,
  Menu,
  Bell,
  AlertTriangle
} from 'lucide-react';

const Navigation: React.FC<{ activeTab: TabType, setActiveTab: (tab: TabType) => void }> = ({ activeTab, setActiveTab }) => {
  const tabs = [
    { id: 'dashboard', label: 'Home', icon: LayoutDashboard },
    { id: 'sales', label: 'Sales', icon: ShoppingCart },
    { id: 'stock', label: 'Stock', icon: Package },
    { id: 'expense', label: 'Expense', icon: Receipt },
    { id: 'customers', label: 'Users', icon: Users },
    { id: 'report', label: 'Reports', icon: BarChart3 },
  ];

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white border-t border-slate-200 flex justify-around items-center px-2 py-2 safe-bottom z-50">
      {tabs.map((tab) => {
        const Icon = tab.icon;
        const isActive = activeTab === tab.id;
        return (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id as TabType)}
            className={`flex flex-col items-center justify-center w-14 py-1 rounded-xl transition-all ${
              isActive ? 'text-indigo-600 bg-indigo-50' : 'text-slate-400 hover:text-slate-600'
            }`}
          >
            <Icon size={22} strokeWidth={isActive ? 2.5 : 2} />
            <span className="text-[10px] mt-1 font-medium">{tab.label}</span>
          </button>
        );
      })}
    </nav>
  );
};

const AppContent: React.FC = () => {
  const [activeTab, setActiveTab] = useState<TabType>('dashboard');
  const { error } = useStore();

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard': return <Dashboard />;
      case 'sales': return <Sales />;
      case 'stock': return <Stock />;
      case 'expense': return <Expenses />;
      case 'customers': return <Customers />;
      case 'report': return <Reports />;
      case 'ai': return <AIAssistant />;
      default: return <Dashboard />;
    }
  };

  return (
    <div className="min-h-screen pb-24 md:pb-0 md:pl-0">
      {/* Error Bar */}
      {error && (
        <div className="bg-rose-600 text-white px-4 py-2 text-[10px] md:text-xs flex items-center justify-center gap-2 sticky top-0 z-[100] animate-in fade-in slide-in-from-top-4">
          <AlertTriangle size={14} />
          <span>{error}</span>
        </div>
      )}

      {/* Mobile Top Bar */}
