import React, { createContext, useContext, useState, useEffect } from 'react';
import { Product, Customer, Sale, Expense, BusinessState } from './types';
import { db } from './services/firebase';
import { 
  collection, 
  onSnapshot, 
  addDoc, 
  updateDoc, 
  deleteDoc, 
  doc, 
  query, 
  orderBy 
} from 'firebase/firestore';

interface StoreContextType extends BusinessState {
  addProduct: (product: Product) => Promise<void>;
  updateProduct: (product: Product) => Promise<void>;
  deleteProduct: (id: string) => Promise<void>;
  addSale: (sale: Sale) => Promise<void>;
  addExpense: (expense: Expense) => Promise<void>;
  updateCustomer: (customer: Customer) => Promise<void>;
  addCustomer: (customer: Customer) => Promise<void>;
  error: string | null;
}

const StoreContext = createContext<StoreContextType | undefined>(undefined);

export const StoreProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [customers, setCustomers] = useState<Customer[]>([]);
  const [sales, setSales] = useState<Sale[]>([]);
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [admin] = useState({
    name: 'Sylsas Admin',
    phone: '0123456789',
    role: 'Owner',
    image: 'https://picsum.photos/seed/admin/100/100'
  });

  useEffect(() => {
    const handleFirestoreError = (err: any) => {
      console.error("Firestore error details:", err);
      if (err.message?.includes('not been used in project') || err.code === 'permission-denied') {
        setError("Firebase Firestore is not enabled. Please go to Firebase Console and Create Database.");
      } else {
        setError("Connection issue: " + err.message);
      }
    };

    try {
      const qProducts = query(collection(db, 'products'));
      const unsubProducts = onSnapshot(qProducts, (snapshot) => {
        setProducts(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Product)));
        setError(null);
      }, handleFirestoreError);

      const qCustomers = query(collection(db, 'customers'));
      const unsubCustomers = onSnapshot(qCustomers, (snapshot) => {
        setCustomers(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Customer)));
      }, handleFirestoreError);

      const qSales = query(collection(db, 'sales'), orderBy('date', 'desc'));
      const unsubSales = onSnapshot(qSales, (snapshot) => {
        setSales(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Sale)));
      }, handleFirestoreError);

      const qExpenses = query(collection(db, 'expenses'), orderBy('date', 'desc'));
      const unsubExpenses = onSnapshot(qExpenses, (snapshot) => {
        setExpenses(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id } as Expense)));
      }, handleFirestoreError);

      return () => {
        unsubProducts();
        unsubCustomers();
        unsubSales();
        unsubExpenses();
      };
    } catch (e: any) {
      handleFirestoreError(e);
    }
  }, []);

  const addProduct = async (product: Product) => {
    try {
      await addDoc(collection(db, 'products'), product);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const updateProduct = async (product: Product) => {
    try {
      const { id, ...data } = product;
      const docRef = doc(db, 'products', id);
      await updateDoc(docRef, data);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const deleteProduct = async (id: string) => {
    try {
      await deleteDoc(doc(db, 'products', id));
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addCustomer = async (customer: Customer) => {
    try {
      await addDoc(collection(db, 'customers'), customer);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const updateCustomer = async (customer: Customer) => {
    try {
      const { id, ...data } = customer;
      const docRef = doc(db, 'customers', id);
      await updateDoc(docRef, data);
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addSale = async (sale: Sale) => {
    try {
      await addDoc(collection(db, 'sales'), sale);

      const product = products.find(p => p.id === sale.productId);
      if (product) {
        const updatedVariants = product.variants.map(v => {
          if (v.size === sale.size && v.color === sale.color) {
            return { ...v, quantity: Math.max(0, v.quantity - sale.quantity) };
          }
          return v;
        });
        await updateDoc(doc(db, 'products', product.id), { variants: updatedVariants });
      }

      const customer = customers.find(c => c.id === sale.customerId);
      if (customer) {
        await updateDoc(doc(db, 'customers', customer.id), {
          totalSpent: customer.totalSpent + sale.totalAmount,
          totalDue: customer.totalDue + sale.dueAmount
        });
      }
    } catch (err: any) {
      setError(err.message);
    }
  };

  const addExpense = async (expense: Expense) => {
    try {
      await addDoc(collection(db, 'expenses'), expense);
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <StoreContext.Provider value={{
      products,
      customers,
      sales,
      expenses,
      admin,
      addProduct,
      updateProduct,
      deleteProduct,
      addSale,
      addExpense,
      updateCustomer,
      addCustomer,
      error
    }}>
      {children}
    </StoreContext.Provider>
  );
};

export const useStore = () => {
  const context = useContext(StoreContext);
  if (!context) throw new Error("useStore must be used within StoreProvider");
  return context;
};

