  addCustomer: (customer: Customer) => Promise<void>;
  error: { message: string; code?: string } | null;
}

const StoreContext = createContext<StoreContextType | undefined>(undefined);

// Helper to load from localStorage
const loadData = <T,>(key: string, defaultValue: T): T => {
  const saved = localStorage.getItem(`sylsas_${key}`);
  return saved ? JSON.parse(saved) : defaultValue;
};

// Helper to save to localStorage
const saveData = <T,>(key: string, data: T) => {
  localStorage.setItem(`sylsas_${key}`, JSON.stringify(data));
};

export const StoreProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [products, setProducts] = useState<Product[]>(() => loadData('products', []));
  const [customers, setCustomers] = useState<Customer[]>(() => loadData('customers', []));
  const [sales, setSales] = useState<Sale[]>(() => loadData('sales', []));
  const [expenses, setExpenses] = useState<Expense[]>(() => loadData('expenses', []));
  const [error, setError] = useState<{ message: string; code?: string } | null>(null);

