    name: 'Sylsas Admin',
    phone: '0123456789',
    role: 'Owner',
    image: 'https://picsum.photos/seed/admin/100/100'
  });

  // Sync state to LocalStorage
  useEffect(() => { saveData('products', products); }, [products]);
