  useEffect(() => { saveData('sales', sales); }, [sales]);
  useEffect(() => { saveData('expenses', expenses); }, [expenses]);

  const addProduct = async (product: Product) => {
    setProducts(prev => [...prev, product]);
  };

  const updateProduct = async (product: Product) => {
    setProducts(prev => prev.map(p => p.id === product.id ? product : p));
  };

  const deleteProduct = async (id: string) => {
    setProducts(prev => prev.filter(p => p.id !== id));
  };

  const addCustomer = async (customer: Customer) => {
    setCustomers(prev => [...prev, customer]);
  };

  const updateCustomer = async (customer: Customer) => {
    setCustomers(prev => prev.map(c => c.id === customer.id ? customer : c));
  };

  const addSale = async (sale: Sale) => {
    // Add sale
    setSales(prev => [sale, ...prev]);

    // Update product stock
    setProducts(prev => prev.map(p => {
      if (p.id === sale.productId) {
        const updatedVariants = p.variants.map(v => 
          (v.size === sale.size && v.color === sale.color) 
            ? { ...v, quantity: Math.max(0, v.quantity - sale.quantity) } 
            : v
        );
        return { ...p, variants: updatedVariants };
      }
      return p;
    }));

    // Update customer stats
    setCustomers(prev => prev.map(c => {
      if (c.id === sale.customerId) {
        return {
          ...c,
          totalSpent: (c.totalSpent || 0) + sale.totalAmount,
          totalDue: (c.totalDue || 0) + sale.dueAmount
        };
      }
      return c;
    }));
  };

  const addExpense = async (expense: Expense) => {
    setExpenses(prev => [expense, ...prev]);
  };

  return (
    <StoreContext.Provider value={{
      products, customers, sales, expenses, admin,
      addProduct, updateProduct, deleteProduct, addSale, addExpense, updateCustomer, addCustomer, error
    }}>
      {children}
    </StoreContext.Provider>
  );
};

export const useStore = () => {
  const context = useContext(StoreContext);
  if (!context) throw new Error("useStore must be used within StoreProvider");
  return context;
};

