import React, { useState } from 'react';
import { useStore } from '../store';
import { Product, Customer, Size, PaymentStatus } from '../types';
import { Plus, Search, UserPlus, CheckCircle2, ChevronRight, X, ArrowLeft, Layers, Target } from 'lucide-react';

const Sales: React.FC = () => {
  const { products, customers, addSale, addCustomer } = useStore();

  const [step, setStep] = useState(1);
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [selectedCustomer, setSelectedCustomer] = useState<Customer | null>(null);
  const [selectedSize, setSelectedSize] = useState<Size | ''>('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [salePrice, setSalePrice] = useState(0);
  const [paidAmount, setPaidAmount] = useState(0);

  const [searchTerm, setSearchTerm] = useState('');
  const [showNewCustomer, setShowNewCustomer] = useState(false);
  const [newCustomer, setNewCustomer] = useState({ name: '', phone: '', address: '' });

  const resetForm = () => {
    setStep(1);
    setSelectedProduct(null);
    setSelectedCustomer(null);
    setSelectedSize('');
    setSelectedColor('');
    setQuantity(1);
    setSalePrice(0);
    setPaidAmount(0);
    setSearchTerm('');
  };

  const handleAddCustomer = () => {
    const customer: Customer = {
      id: Date.now().toString(),
      ...newCustomer,
      totalSpent: 0,
      totalDue: 0
    };
    addCustomer(customer);
    setSelectedCustomer(customer);
    setShowNewCustomer(false);
    setStep(2);
  };

  const handleCompleteSale = () => {
    if (!selectedProduct || !selectedCustomer || !selectedSize || !selectedColor) return;

    const totalAmount = salePrice * quantity;
    const dueAmount = totalAmount - paidAmount;
    const profit = (salePrice - selectedProduct.purchasePrice) * quantity;
    const paymentStatus: PaymentStatus = dueAmount === 0 ? 'Full Paid' : (paidAmount === 0 ? 'Due' : 'Partial Paid');

    addSale({
      id: Date.now().toString(),
      customerId: selectedCustomer.id,
      customerName: selectedCustomer.name,
      productId: selectedProduct.id,
      productName: selectedProduct.name,
      size: selectedSize as Size,
      color: selectedColor,
      quantity,
      salePrice,
      totalAmount,
      paidAmount,
      dueAmount,
      profit,
      date: new Date().toISOString(),
      paymentStatus
    });

    setStep(6);
  };

  const currentVariant = selectedProduct?.variants.find(v => v.size === selectedSize && v.color === selectedColor);
  const maxQty = currentVariant?.quantity || 0;

  const StepHeader = ({ title, subtitle }: { title: string, subtitle: string }) => (
    <div className="mb-6">
      <div className="flex items-center gap-3 mb-1">
        {step > 1 && step < 6 && (
          <button onClick={() => setStep(step - 1)} className="p-2 bg-slate-100 rounded-xl active:scale-90 transition-transform">
            <ArrowLeft size={18} />
          </button>
        )}
        <h2 className="text-2xl font-black text-slate-900 tracking-tight">{title}</h2>
      </div>
      <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">{subtitle}</p>
    </div>
  );

  return (
    <div className="space-y-6 max-w-2xl mx-auto">
      {/* Progress Track */}
      {step < 6 && (
        <div className="flex items-center gap-2 mb-8">
          {[1, 2, 3, 4, 5].map(s => (
            <div key={s} className={`h-2 flex-1 rounded-full transition-all duration-500 ${step >= s ? 'bg-indigo-600 shadow-sm' : 'bg-slate-200'}`}></div>
          ))}
        </div>
      )}

      {/* STEP 1: CUSTOMER SELECTION */}
      {step === 1 && (
        <div className="animate-in slide-in-from-bottom-6">
          <StepHeader title="Client Selection" subtitle="Step 01: Who is buying?" />

          <div className="flex items-center gap-3 mb-6">
            <div className="relative flex-1">
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
              <input 
                type="text" 
                placeholder="Search phone or name..." 
                className="w-full pl-12 pr-4 py-4 bg-white border border-slate-100 rounded-3xl outline-none focus:ring-4 focus:ring-indigo-500/10 shadow-sm transition-all"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <button 
              onClick={() => setShowNewCustomer(true)}
              className="p-4 bg-indigo-600 text-white rounded-[1.5rem] hover:bg-indigo-700 shadow-lg shadow-indigo-200 active:scale-90 transition-transform"
            >
              <UserPlus size={24} />
            </button>
          </div>

          <div className="space-y-3">
            {customers
              .filter(c => c.name.toLowerCase().includes(searchTerm.toLowerCase()) || c.phone.includes(searchTerm))
              .map(customer => (
                <button 
                  key={customer.id} 
                  onClick={() => { setSelectedCustomer(customer); setStep(2); }}
                  className="w-full bg-white p-5 rounded-[1.5rem] border border-white flex items-center justify-between shadow-sm active:scale-[0.98] transition-all hover:shadow-md group"
                >
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-slate-50 rounded-2xl flex items-center justify-center text-slate-400 group-hover:bg-indigo-50 group-hover:text-indigo-600">
                      <Layers size={22} />
                    </div>
                    <div className="text-left">
                      <p className="font-extrabold text-slate-900">{customer.name}</p>
                      <p className="text-xs font-bold text-slate-400">{customer.phone}</p>
                    </div>
                  </div>
                  <ChevronRight size={18} className="text-slate-300" />
                </button>
              ))}
          </div>
        </div>
      )}

      {/* STEP 2: PRODUCT SELECTION */}
      {step === 2 && (
        <div className="animate-in slide-in-from-bottom-6">
          <StepHeader title="Catalog" subtitle="Step 02: Pick an item" />
          <div className="grid grid-cols-2 gap-4">
            {products.map(product => {
              const totalStock = product.variants.reduce((acc, v) => acc + v.quantity, 0);
              return (
                <button 
                  key={product.id} 
                  onClick={() => { setSelectedProduct(product); setSalePrice(product.salePrice); setStep(3); }}
                  className="bg-white p-4 rounded-[2rem] border border-white shadow-sm text-left active:scale-[0.98] transition-all group"
                >
                  <div className="relative overflow-hidden rounded-[1.5rem] mb-4 aspect-square">
                    <img src={product.image} alt={product.name} className="w-full h-full object-cover transition-transform group-hover:scale-110" />
                    <div className="absolute top-2 right-2 bg-white/90 backdrop-blur-md px-2 py-1 rounded-xl text-[9px] font-black uppercase shadow-sm">
                      {totalStock} in Stock
                    </div>
                  </div>
                  <h4 className="font-extrabold text-slate-900 text-sm truncate">{product.name}</h4>
                  <p className="text-indigo-600 font-black mt-1">‡ß≥{product.salePrice}</p>
                </button>
              );
            })}
          </div>
        </div>
      )}

      {/* STEP 3: SIZE & COLOR */}
      {step === 3 && selectedProduct && (
        <div className="animate-in slide-in-from-bottom-6">
          <StepHeader title="Configuration" subtitle={`Step 03: ${selectedProduct.name}`} />
          <div className="bg-white p-8 rounded-[2.5rem] border border-white shadow-xl">
            <div className="mb-8">
              <label className="block text-xs font-black text-slate-400 uppercase tracking-widest mb-4">Available Sizes</label>
              <div className="flex flex-wrap gap-3">
                {['XS', 'S', 'M', 'L', 'XL', 'XXL'].map(sz => {
                  const available = selectedProduct.variants.some(v => v.size === sz && v.quantity > 0);
                  return (
                    <button 
                      key={sz}
                      disabled={!available}
                      onClick={() => setSelectedSize(sz as Size)}
                      className={`w-14 h-14 rounded-2xl border-2 flex items-center justify-center font-black text-sm transition-all active:scale-90 ${
                        selectedSize === sz 
                          ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100' 
                          : available ? 'bg-white border-slate-100 text-slate-700' : 'bg-slate-50 border-slate-50 text-slate-200 cursor-not-allowed'
                      }`}
                    >
                      {sz}
                    </button>
                  );
                })}
              </div>
            </div>

            {selectedSize && (
              <div className="animate-in fade-in slide-in-from-top-4">
                <label className="block text-xs font-black text-slate-400 uppercase tracking-widest mb-4">Select Shade</label>
                <div className="flex flex-wrap gap-3">
                  {selectedProduct.variants
                    .filter(v => v.size === selectedSize && v.quantity > 0)
                    .map(v => (
                      <button 
                        key={v.color}
                        onClick={() => setSelectedColor(v.color)}
                        className={`px-6 py-3 rounded-2xl border-2 text-sm font-black transition-all active:scale-90 ${
                          selectedColor === v.color 
                            ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100' 
                            : 'bg-white border-slate-100 text-slate-700'
                        }`}
                      >
                        {v.color} <span className="opacity-50 ml-2">({v.quantity})</span>
                      </button>
                    ))}
                </div>
              </div>
            )}

            <button 
              disabled={!selectedSize || !selectedColor}
              onClick={() => setStep(4)}
              className="w-full mt-10 py-5 bg-indigo-600 text-white rounded-3xl font-black text-lg shadow-xl shadow-indigo-100 disabled:bg-slate-100 disabled:text-slate-300 disabled:shadow-none transition-all active:scale-95"
            >
              Set Quantity & Price
            </button>
          </div>
        </div>
      )}

      {/* STEP 4: QUANTITY & PRICE */}
      {step === 4 && (
        <div className="animate-in slide-in-from-bottom-6">
          <StepHeader title="Adjust Values" subtitle="Step 04: Finalizing total" />
          <div className="bg-white p-8 rounded-[2.5rem] border border-white shadow-xl">
            <div className="flex items-center gap-5 p-5 bg-slate-50 rounded-3xl mb-8 border border-slate-100">
              <img src={selectedProduct?.image} className="w-20 h-20 rounded-2xl object-cover shadow-sm" />
              <div>
                <h4 className="font-black text-slate-900 text-lg leading-tight">{selectedProduct?.name}</h4>
                <p className="text-[10px] font-bold text-slate-400 uppercase mt-1 tracking-widest">{selectedSize} ‚Ä¢ {selectedColor} ‚Ä¢ {maxQty} left</p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-6 mb-8">
              <div className="space-y-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Amount</label>
                <input 
                  type="number" min="1" max={maxQty} value={quantity}
                  onChange={(e) => setQuantity(Number(e.target.value))}
                  className="w-full p-5 bg-slate-50 border border-slate-100 rounded-3xl outline-none focus:ring-4 focus:ring-indigo-500/10 font-black text-xl text-center"
                />
              </div>
              <div className="space-y-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Unit Price</label>
                <input 
                  type="number" value={salePrice}
                  onChange={(e) => setSalePrice(Number(e.target.value))}
                  className="w-full p-5 bg-slate-50 border border-slate-100 rounded-3xl outline-none focus:ring-4 focus:ring-indigo-500/10 font-black text-xl text-indigo-600 text-center"
                />
