            </div>

            <div className="p-8 bg-indigo-600 rounded-[2rem] text-center shadow-2xl shadow-indigo-100 mb-8">
              <p className="text-indigo-100/60 text-[10px] font-bold uppercase tracking-[0.2em] mb-1">Total Bill</p>
              <h3 className="text-4xl font-black text-white">‡ß≥{(salePrice * quantity).toLocaleString()}</h3>
            </div>

            <button 
              disabled={quantity > maxQty || quantity < 1}
              onClick={() => { setPaidAmount(salePrice * quantity); setStep(5); }}
              className="w-full py-5 bg-indigo-800 text-white rounded-3xl font-black text-lg shadow-xl active:scale-95 transition-all"
            >
              Checkout Now
            </button>
          </div>
        </div>
