      {/* STEP 5: PAYMENT */}
      {step === 5 && (
        <div className="animate-in slide-in-from-bottom-6">
          <StepHeader title="Payment" subtitle="Step 05: How much was paid?" />
          <div className="bg-white p-8 rounded-[2.5rem] border border-white shadow-xl">
            <div className="text-center mb-8">
              <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-1">Due from {selectedCustomer?.name}</p>
              <h3 className="text-4xl font-black text-slate-900">‡ß≥{(salePrice * quantity).toLocaleString()}</h3>
            </div>

            <div className="space-y-6">
              <div className="space-y-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest text-center block">Collected Cash</label>
                <input 
                  type="number" value={paidAmount}
                  onChange={(e) => setPaidAmount(Number(e.target.value))}
                  className="w-full p-6 bg-slate-50 border border-slate-100 rounded-[2rem] outline-none focus:ring-4 focus:ring-emerald-500/10 text-3xl font-black text-center text-emerald-600 shadow-inner"
                />
              </div>

              <div className="grid grid-cols-3 gap-3">
                {[
                  { label: '100%', val: salePrice * quantity },
                  { label: '50%', val: (salePrice * quantity) / 2 },
                  { label: 'None', val: 0 }
                ].map(p => (
                  <button key={p.label} onClick={() => setPaidAmount(p.val)} className="py-3 bg-slate-50 border border-slate-100 rounded-2xl text-[10px] font-black text-slate-500 uppercase tracking-widest active:scale-90 transition-all">
                    {p.label}
