              <div className={`p-6 rounded-3xl flex justify-between items-center border-2 transition-colors ${
                (salePrice * quantity) - paidAmount > 0 ? 'bg-rose-50 border-rose-100 text-rose-600' : 'bg-emerald-50 border-emerald-100 text-emerald-600'
              }`}>
                <p className="font-black text-sm uppercase tracking-wider">Remaining Balance</p>
                <p className="text-2xl font-black">‡ß≥{((salePrice * quantity) - paidAmount).toLocaleString()}</p>
