      {/* NEW CUSTOMER MODAL */}
      {showNewCustomer && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-md flex items-end sm:items-center justify-center z-[60] p-4">
          <div className="bg-white w-full max-w-md rounded-[2.5rem] p-8 animate-in slide-in-from-bottom-20 duration-500">
            <div className="flex justify-between items-center mb-8">
              <h3 className="text-2xl font-black text-slate-900 tracking-tight">New Client</h3>
              <button onClick={() => setShowNewCustomer(false)} className="p-2 bg-slate-50 rounded-xl"><X size={20} className="text-slate-400" /></button>
