                        updated[idx].size = e.target.value as Size;
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    >
                      {['XS', 'S', 'M', 'L', 'XL', 'XXL'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>
                    <input 
                      type="text" placeholder="Color" className="col-span-5 p-3 bg-slate-50 border border-slate-100 rounded-2xl text-xs font-bold outline-none"
                      value={v.color}
                      onChange={(e) => {
                        const updated = [...(newProduct.variants || [])];
                        updated[idx].color = e.target.value;
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    />
                    <input 
                      type="number" placeholder="Qty" className="col-span-4 p-3 bg-slate-50 border border-slate-100 rounded-2xl text-xs font-black outline-none"
                      value={v.quantity}
                      onChange={(e) => {
                        const updated = [...(newProduct.variants || [])];
                        updated[idx].quantity = Number(e.target.value);
                        setNewProduct({...newProduct, variants: updated});
                      }}
                    />
                  </div>
                ))}
              </div>
            </div>

            <button 
              onClick={handleSave}
              className="w-full py-6 bg-indigo-600 text-white rounded-[2rem] font-black text-xl shadow-2xl shadow-indigo-100 mt-6 active:scale-95 transition-all"
            >
              Upload to Inventory
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stock;

import React, { useState, useRef, useEffect } from 'react';
import { useStore } from '../store';
import { getBusinessInsights } from '../services/gemini';
import { Send, Sparkles, User, Bot, Command, ChevronRight } from 'lucide-react';

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

const AIAssistant: React.FC = () => {
  const state = useStore();
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: 'Hello! I am Sylsas Intelligence. Ask me anything about your inventory, daily sales, or growth strategies.',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMsg: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await getBusinessInsights(state, input);
      const aiMsg: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: response,
        timestamp: new Date()
      };
      setMessages(prev => [...prev, aiMsg]);
    } catch (err) {
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const suggestions = [
    "Revenue analysis",
    "Stock alerts",
    "Profit margin?",
    "Today's summary"
  ];

  return (
    <div className="flex flex-col h-[calc(100vh-160px)] max-w-2xl mx-auto">
      {/* AI Header */}
      <div className="bg-white/40 backdrop-blur-xl border border-white p-6 rounded-[2.5rem] mb-6 flex items-center justify-between shadow-xl shadow-indigo-100/20">
        <div className="flex items-center gap-4">
          <div className="w-14 h-14 bg-gradient-to-tr from-indigo-600 to-violet-600 rounded-[1.5rem] flex items-center justify-center text-white shadow-xl shadow-indigo-200 relative overflow-hidden">
            <Bot size={28} />
            <div className="absolute inset-0 bg-white/20 animate-pulse"></div>
          </div>
          <div>
            <h2 className="text-xl font-black text-slate-900 tracking-tight leading-none">Intelligence</h2>
            <p className="text-[10px] font-black text-indigo-500 uppercase tracking-widest mt-1.5 flex items-center gap-1">
              <Sparkles size={10} strokeWidth={3} /> Pro Advisor Active
            </p>
          </div>
