interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

const AIAssistant: React.FC = () => {
  const state = useStore();
  const t = AI_T[state.language];
  const [messages, setMessages] = useState<Message[]>([
    { id: '1', role: 'assistant', content: t.welcome, timestamp: new Date() }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    scrollRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, isLoading]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;
    const userMsg: Message = { id: Date.now().toString(), role: 'user', content: input, timestamp: new Date() };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setIsLoading(true);
    try {
      const response = await getBusinessInsights(state, input);
      setMessages(prev => [...prev, { id: (Date.now()+1).toString(), role: 'assistant', content: response, timestamp: new Date() }]);
    } catch (err) { console.error(err); } finally { setIsLoading(false); }
  };

  return (
    <div className="flex flex-col h-[calc(100vh-160px)] max-w-2xl mx-auto">
      <div className="bg-white/40 backdrop-blur-xl border border-white p-6 rounded-[2.5rem] mb-6 flex items-center justify-between shadow-xl shadow-indigo-100/20">
        <div className="flex items-center gap-4">
          <div className="w-14 h-14 bg-gradient-to-tr from-indigo-600 to-violet-600 rounded-[1.5rem] flex items-center justify-center text-white shadow-xl relative overflow-hidden">
            <Bot size={28} />
          </div>
          <div>
            <h2 className="text-xl font-black text-slate-900 leading-none">{t.title}</h2>
            <p className="text-[10px] font-black text-indigo-500 uppercase mt-1.5 flex items-center gap-1"><Sparkles size={10} strokeWidth={3} /> {t.status}</p>
          </div>
        </div>
        <div className="bg-emerald-50 text-emerald-600 px-3 py-1 rounded-full text-[9px] font-black uppercase tracking-widest">{t.online}</div>
      </div>
      <div className="flex-1 overflow-y-auto px-1 space-y-6 no-scrollbar pb-10">
        {messages.map((msg, idx) => (
          <div key={msg.id} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-4 duration-500`} style={{animationDelay: `${idx * 50}ms`}}>
            <div className={`max-w-[85%] flex gap-3 ${msg.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
              <div className={`w-8 h-8 shrink-0 rounded-2xl flex items-center justify-center shadow-sm ${msg.role === 'user' ? 'bg-indigo-600 text-white' : 'bg-white text-indigo-600 border'}`}>
                {msg.role === 'user' ? <User size={16} /> : <Command size={16} />}
              </div>
              <div className={`p-5 rounded-[2rem] text-sm font-medium leading-relaxed shadow-sm ${msg.role === 'user' ? 'bg-indigo-600 text-white rounded-tr-none' : 'bg-white text-slate-800 border rounded-tl-none whitespace-pre-wrap'}`}>
                {msg.content}
              </div>
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white/80 border p-5 rounded-[2rem] rounded-tl-none shadow-sm flex items-center gap-3">
              <div className="flex gap-1.5"><div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce"></div><div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce [animation-delay:0.2s]"></div><div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce [animation-delay:0.4s]"></div></div>
              <span className="text-[10px] text-slate-400 font-black uppercase">{t.loading}</span>
            </div>
          </div>
        )}
        <div ref={scrollRef} />
      </div>
      <div className="mt-4">
        <div className="flex gap-2 overflow-x-auto pb-4 no-scrollbar">
          {t.suggestions.map(s => (
            <button key={s} onClick={() => setInput(s)} className="whitespace-nowrap px-5 py-2.5 bg-white text-slate-600 rounded-full text-[10px] font-black uppercase tracking-widest border shadow-sm hover:bg-indigo-50 active:scale-95 transition-all">{s}</button>
          ))}
        </div>
        <div className="flex items-center gap-3 p-3 bg-white/80 rounded-[2rem] border shadow-2xl">
          <input type="text" placeholder={t.placeholder} className="flex-1 bg-transparent px-4 py-2 text-sm font-bold outline-none" value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={(e) => e.key === 'Enter' && handleSend()} />
          <button onClick={handleSend} disabled={!input.trim() || isLoading} className="w-12 h-12 bg-indigo-600 text-white rounded-[1.2rem] flex items-center justify-center shadow-xl active:scale-90 transition-all"><Send size={20} /></button>
        </div>
      </div>
    </div>
  );
};

