                  <button onClick={addVariantField} className="bg-indigo-50 text-indigo-600 px-3 py-1.5 rounded-xl text-[10px] font-black">+ NEW</button>
                </div>
                {newProduct.variants?.map((v, idx) => (
                  <div key={idx} className="flex gap-3">
                    <select className="p-3 bg-slate-50 rounded-2xl text-xs font-black" value={v.size} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].size = e.target.value as Size; setNewProduct({...newProduct, variants: u}); }}>
                      {['XS', 'S', 'M', 'L', 'XL', 'XXL'].map(s => <option key={s} value={s}>{s}</option>)}
                    </select>
                    <input type="text" placeholder="Color" className="flex-1 p-3 bg-slate-50 rounded-2xl text-xs font-bold" value={v.color} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].color = e.target.value; setNewProduct({...newProduct, variants: u}); }} />
                    <input type="number" placeholder="Qty" className="w-20 p-3 bg-slate-50 rounded-2xl text-xs font-black" value={v.quantity} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].quantity = Number(e.target.value); setNewProduct({...newProduct, variants: u}); }} />
                  </div>
                ))}
              </div>
            </div>
            <button onClick={handleSave} className="w-full py-6 bg-indigo-600 text-white rounded-[2rem] font-black text-xl shadow-2xl mt-8 active:scale-95 transition-all">
              {editingId ? t.update : t.save}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

