import React, { createContext, useContext, useState, useEffect } from 'react';
import { Product, Customer, Sale, Expense, BusinessState, Language } from './types';

interface StoreContextType extends BusinessState {
  addProduct: (product: Product) => Promise<void>;
  updateProduct: (product: Product) => Promise<void>;
  deleteProduct: (id: string) => Promise<void>;
  addSale: (sale: Sale) => Promise<void>;
  deleteSale: (id: string) => Promise<void>;
  addExpense: (expense: Expense) => Promise<void>;
  updateExpense: (expense: Expense) => Promise<void>;
  deleteExpense: (id: string) => Promise<void>;
  addCustomer: (customer: Customer) => Promise<void>;
  updateCustomer: (customer: Customer) => Promise<void>;
  deleteCustomer: (id: string) => Promise<void>;
  receivePayment: (customerId: string, amount: number) => Promise<void>;
  setLanguage: (lang: Language) => void;
  login: (pass: string) => boolean;
  logout: () => void;
  error: { message: string; code?: string } | null;
}

const StoreContext = createContext<StoreContextType | undefined>(undefined);

const loadData = <T,>(key: string, defaultValue: T): T => {
  const saved = localStorage.getItem(`sylsas_${key}`);
  return saved ? JSON.parse(saved) : defaultValue;
};

const saveData = <T,>(key: string, data: T) => {
  localStorage.setItem(`sylsas_${key}`, JSON.stringify(data));
};

export const StoreProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [products, setProducts] = useState<Product[]>(() => loadData('products', []));
  const [customers, setCustomers] = useState<Customer[]>(() => loadData('customers', []));
  const [sales, setSales] = useState<Sale[]>(() => loadData('sales', []));
  const [expenses, setExpenses] = useState<Expense[]>(() => loadData('expenses', []));
  const [language, setLanguage] = useState<Language>(() => loadData('language', 'en'));
  const [isLoggedIn, setIsLoggedIn] = useState<boolean>(() => loadData('isLoggedIn', false));
  const [error, setError] = useState<{ message: string; code?: string } | null>(null);

  const [admin, setAdmin] = useState(() => loadData('admin', {
    name: 'Sylsas Admin',
    phone: '01712345678',
    role: 'Owner',
    image: 'https://picsum.photos/seed/admin/100/100'
  }));

  useEffect(() => { saveData('products', products); }, [products]);
  useEffect(() => { saveData('customers', customers); }, [customers]);
  useEffect(() => { saveData('sales', sales); }, [sales]);
  useEffect(() => { saveData('expenses', expenses); }, [expenses]);
  useEffect(() => { saveData('language', language); }, [language]);
  useEffect(() => { saveData('isLoggedIn', isLoggedIn); }, [isLoggedIn]);
  useEffect(() => { saveData('admin', admin); }, [admin]);

  const login = (pass: string) => {
    if (pass === '1234') {
      setIsLoggedIn(true);
      return true;
    }
    return false;
  };

  const logout = () => setIsLoggedIn(false);

  const addProduct = async (product: Product) => setProducts(prev => [...prev, product]);
  const updateProduct = async (product: Product) => setProducts(prev => prev.map(p => p.id === product.id ? product : p));
  const deleteProduct = async (id: string) => setProducts(prev => prev.filter(p => p.id !== id));

  const addCustomer = async (customer: Customer) => setCustomers(prev => [...prev, customer]);
  const updateCustomer = async (customer: Customer) => setCustomers(prev => prev.map(c => c.id === customer.id ? customer : c));
  const deleteCustomer = async (id: string) => setCustomers(prev => prev.filter(c => c.id !== id));

  const addExpense = async (expense: Expense) => setExpenses(prev => [expense, ...prev]);
  const updateExpense = async (expense: Expense) => setExpenses(prev => prev.map(e => e.id === expense.id ? expense : e));
  const deleteExpense = async (id: string) => setExpenses(prev => prev.filter(e => e.id !== id));

  const addSale = async (sale: Sale) => {
    setSales(prev => [sale, ...prev]);
    setProducts(prev => prev.map(p => {
      if (p.id === sale.productId) {
        return {
          ...p,
          variants: p.variants.map(v => 
            (v.size === sale.size && v.color === sale.color) 
              ? { ...v, quantity: Math.max(0, v.quantity - sale.quantity) } 
              : v
          )
        };
      }
      return p;
    }));
    setCustomers(prev => prev.map(c => {
      if (c.id === sale.customerId) {
        return {
          ...c,
          totalSpent: (c.totalSpent || 0) + sale.totalAmount,
          totalDue: (c.totalDue || 0) + sale.dueAmount
        };
      }
      return c;
    }));
  };

  const receivePayment = async (customerId: string, amount: number) => {
    setCustomers(prev => prev.map(c => {
      if (c.id === customerId) {
        return {
          ...c,
          totalDue: Math.max(0, c.totalDue - amount)
        };
      }
      return c;
    }));

    // Find first sale with debt to apply payment visually
    setSales(prev => {
      let remaining = amount;
      return prev.map(s => {
        if (s.customerId === customerId && s.dueAmount > 0 && remaining > 0) {
          const payToThis = Math.min(s.dueAmount, remaining);
          remaining -= payToThis;
          const newDue = s.dueAmount - payToThis;
          return {
            ...s,
            dueAmount: newDue,
            paidAmount: s.paidAmount + payToThis,
            paymentStatus: newDue === 0 ? 'Full Paid' : 'Partial Paid'
          };
        }
        return s;
      });
    });
  };

  const deleteSale = async (id: string) => {
    const sale = sales.find(s => s.id === id);
    if (!sale) return;
    setProducts(prev => prev.map(p => {
      if (p.id === sale.productId) {
        return {
          ...p,
          variants: p.variants.map(v => 
            (v.size === sale.size && v.color === sale.color) 
              ? { ...v, quantity: v.quantity + sale.quantity } 
              : v
          )
        };
      }
      return p;
    }));
    setCustomers(prev => prev.map(c => {
      if (c.id === sale.customerId) {
        return {
          ...c,
          totalSpent: Math.max(0, (c.totalSpent || 0) - sale.totalAmount),
          totalDue: Math.max(0, (c.totalDue || 0) - sale.dueAmount)
        };
      }
      return c;
    }));
    setSales(prev => prev.filter(s => s.id !== id));
  };

  return (
    <StoreContext.Provider value={{
      products, customers, sales, expenses, admin, language, isLoggedIn,
      addProduct, updateProduct, deleteProduct, addSale, deleteSale, 
      addExpense, updateExpense, deleteExpense, 
      addCustomer, updateCustomer, deleteCustomer, receivePayment,
      setLanguage, login, logout, error
    }}>
      {children}
    </StoreContext.Provider>
  );
};

export const useStore = () => {
  const context = useContext(StoreContext);
  if (!context) throw new Error("useStore must be used within StoreProvider");
  return context;
};

