              </div>

              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">{t.cost}</label>
                <input type="number" placeholder="0.00" className="w-full p-6 bg-slate-50 border-0 rounded-[2rem] outline-none font-black text-xl focus:ring-4 focus:ring-indigo-100" value={newProduct.purchasePrice || ''} onChange={e => setNewProduct({...newProduct, purchasePrice: Number(e.target.value)})} />
              </div>

              <div className="space-y-4">
                <div className="flex items-center justify-between px-4">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Inventory Variants</label>
                  <button onClick={addVariantField} className="bg-indigo-600 text-white px-4 py-2 rounded-xl text-[10px] font-black shadow-lg shadow-indigo-100">+ ADD SIZE/COLOR</button>
                </div>
                <div className="space-y-3">
                  {newProduct.variants?.map((v, idx) => (
                    <div key={idx} className="bg-slate-50 p-4 rounded-3xl space-y-3 border border-slate-100 animate-in slide-in-from-left-4" style={{animationDelay: `${idx * 50}ms`}}>
                      <div className="flex gap-3">
                        <div className="flex-1 space-y-1">
                           <span className="text-[8px] font-black text-slate-400 uppercase ml-2">Size</span>
                           <div className="relative">
                            <select className="w-full p-3 bg-white rounded-2xl text-[10px] font-black uppercase outline-none appearance-none" value={v.size} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].size = e.target.value as Size; setNewProduct({...newProduct, variants: u}); }}>
                              {AVAILABLE_SIZES.map(s => <option key={s} value={s}>{s}</option>)}
                            </select>
                            <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300" size={12} />
                           </div>
                        </div>
                        <div className="flex-1 space-y-1">
                           <span className="text-[8px] font-black text-slate-400 uppercase ml-2">Color</span>
                           <div className="relative">
                            <select className="w-full p-3 bg-white rounded-2xl text-[10px] font-black uppercase outline-none appearance-none" value={v.color} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].color = e.target.value; setNewProduct({...newProduct, variants: u}); }}>
                              {COMMON_COLORS.map(c => <option key={c} value={c}>{c}</option>)}
                              {!COMMON_COLORS.includes(v.color) && <option value={v.color}>{v.color}</option>}
                            </select>
                            <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300" size={12} />
                           </div>
                        </div>
                        <div className="w-24 space-y-1">
                           <span className="text-[8px] font-black text-slate-400 uppercase ml-2">Qty</span>
                           <input type="number" placeholder="Qty" className="w-full p-3 bg-white rounded-2xl text-[10px] font-black text-center outline-none" value={v.quantity} onChange={e => { const u = [...(newProduct.variants || [])]; u[idx].quantity = Number(e.target.value); setNewProduct({...newProduct, variants: u}); }} />
                        </div>
                        <button onClick={() => { const u = [...(newProduct.variants || [])]; u.splice(idx, 1); setNewProduct({...newProduct, variants: u}); }} className="mt-5 p-3 text-rose-400 hover:text-rose-600"><Trash2 size={16} /></button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <button onClick={handleSave} className="w-full py-6 bg-indigo-600 text-white rounded-[2rem] font-black text-xl shadow-2xl shadow-indigo-100 mt-12 active:scale-95 transition-all">
              {editingId ? t.update : t.save}
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

