  const { sales, expenses, products, language } = useStore();
  const t = REPORTS_T[language];

  // Prepare data for Daily Sales Profit vs Expense
  const last7Days = [...Array(7)].map((_, i) => {
    const d = new Date();
    d.setDate(d.getDate() - i);
